mapper.select:
SELECT userId, password, nickName, createTime, isWithDraw, status
FROM user
WHERE userId = #{id}

mapper.update:
UPDATE user
SET password = #{password},
    nickName = #{nickName},
    isWithDraw = #{isWithDraw},
    status = #{status}
WHERE id = #{id}

mapper.select:
SELECT userId, password, nickName, createTime, isWithDraw, status
FROM user
WHERE userId = #{id}
    AND password = #{password}
    AND status != 'DELETE'

SELECT COUNT(id)
FROM user
WHERE userId = #{userId}

mapper.update:
UPDATE user
SET password = #{password}
WHERE userId = #{userId}
